class page extends StatefulWidget {
  final String data;
  final String maquina;
  final String imagem;

  page({Key key, @required this.data, @required this.maquina,@required this.imagem})
      : super(key: key);
  @override
  _pageState createState() => _pageState(data, maquina, imagem);
}

class _pageState extends State<page> {
  final String data;
  final String maquina;
  final String imagem;
  _pageState(this.data, this.maquina, this.imagem);

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      body: Column(
        children: <Widget>[
          Expanded(
            flex: 3,
            child: Container(
              padding: EdgeInsets.all(15.0),
              alignment: Alignment.bottomLeft,
              child: ListView(
                children: <Widget>[
                  Image(
                    image: AssetImage(
                      imagem,   //Imagem da máquina
                    ),
                  ),
                  Text(
                    data, //Texto sobre a máquina
                    style: TextStyle(
                      fontSize: 16.0,
                    ),
                    textAlign: TextAlign.justify,
                  ),
                ],
              ),
            ),
          ),
        ],
      ),
      
      appBar: AppBar(
        title: Text(
          //"Máquinas",
          maquina,
          style: TextStyle(
            fontSize: 20.0,
          ),
        ),
        centerTitle: true,
        leading: GestureDetector(
          onTap: () {
            Navigator.of(context).pushReplacement(MaterialPageRoute(
              builder: (context) => maquinas(),
            ));
          },
          child: Icon(Icons.arrow_back),
        ),
      ),

      bottomNavigationBar: BottomAppBar(
        child: Row(
          mainAxisSize: MainAxisSize.max,
          mainAxisAlignment: MainAxisAlignment.spaceBetween,
          children: <Widget>[
            SizedBox(width: 7),
            IconButton(
              icon: Icon(
                Icons.home,
                size: 24.0,
              ),
              onPressed: () => Navigator.of(context).pushReplacement(MaterialPageRoute(
                builder: (context) => home(),
              )),
            ),
            SizedBox(width: 7),
          ],
        ),
        color: Theme.of(context).primaryColor,
        shape: CircularNotchedRectangle(),
      ),
    );
  }
}
